  const [userName, changeUserName] = useState("");
  const [userTeam, changeUserTeam] = useState("");
  const [userRank, changeUserRank] = useState("");

  //로그인 여부 나타내는 함수
  const checkLogin = (id,pw) =>{
    //임시로 sejin, 1234로 해놓음. 나중에는 백엔드랑 소통해야함
    if (id==="sejin" && pw==="1234"){
      isLogin = true;

      ////// 삭제 예정
      changeUserName("김세진");
      changeUserTeam("생산관리 1팀");
      changeUserRank("사원");
      //////
    }else{

    }
  };


  return (
    <div 
      style = 
      {{display : "flex",
        flexDirection : "column",

      }}>

      <NavBar userNameFromApp={userName} userTeamFromApp={userTeam} userRankFromApp={userRank} /> 
      {isLogin? null : <Login checkLogin={checkLogin}/>}

    </div>
  );
}

export default App;
