import {useEffect, useState} from "react";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import { faUser, faHouseChimney } from "@fortawesome/free-solid-svg-icons";


function NavBar () {
    const [userName, changeUserName] = useState("홍길동");
    const [userTeam, changeUserTeam] = useState("생산관리 1팀");
    const [userRank, changeUserRank] = useState("사원");

    return (
        <section id="navbar">
            <div class="navbar__upper">
                <div class="navbar__title">
                    <FontAwesomeIcon icon={faHouseChimney} id="navbar__title__logo" />
                    <h1>생산 준비 현황</h1>
            </div>
                <div class="navbar__userInfo">
                    <FontAwesomeIcon icon={faUser} id="navbar__userInfo__logo"/>
                    <h3 class="navbar__userInfo__name">{userName}</h3>
                    <span class="navbar__userInfo__about">{userTeam} / {userRank}</span>
                </div>
            </div>

        </section>
    );
};

export default NavBar;
