import {useEffect, useState, useMemo} from "react";
import ApprovelistTable1 from './ApprovelistTable1.js';

function Approvelist({onSubmit}){
    
    const columns = useMemo(()=>[
        {
            accessor: "division",
            Header: "구분",
        },
        {
            accessor: "material_type",
            Header: "자재 유형",
        },
        {
            accessor: "material_code",
            Header: "자재",
        },
        {
            accessor: "material_description",
            Header: "자재 내역",
        },
        {
            accessor: "produce_history",
            Header: "제품 지시 기록서",
        },
        {
            accessor: "produce_history_date",
            Header: "날짜",
        },
        {
            accessor: "produce_standard",
            Header: "제품표준서",
        },
        {
            accessor: "produce_standard_date",
            Header: "날짜",
        },
        {
            accessor: "planning",
            Header: "생산 계획서",
        },
        {
            accessor: "planning_date",
            Header: "날짜",
        },
    ],[]);

    const [data,updateData] = useState([{
        division:"계획", material_type:"HALB", material_code:"8000004", material_description:"에어탈정(아세클로페낙)반제품", 
        produce_history:"승인", produce_history_date:"2022.10.22", produce_standard:"미승인", produce_standard_date:"2022.10.30", planning:"승인", planning_date:"2022.09.27", 
        },
        {division:"계획", material_type:"HALB", material_code:"7089124", material_description:"아스피린 반제품", 
        produce_history:"승인", produce_history_date:"2022.11.29", produce_standard:"승인", produce_standard_date:"2022.11.30", planning:"승인", planning_date:"2022.11.30", 
        }
    ]); //데이터


    const changeData = (row, column, type) =>{
        //type이 0이면 승인 데이터를 미승인으로, 1이면 미승인 데이터를 승인으로
        if (type===0){
            if (column===4){
                const tmpData = data.map((element,index) =>{
                    if (index===row){
                        element.produce_history="미승인";
                    }
                })                
                updateData(tmpData);
            }else if (column===6){
                const tmpData = data.map((element,index) =>{
                    if (index===row){
                        element.produce_standard="미승인";
                    }
                })                
                updateData(tmpData);
            }else{
                const tmpData = data.map((element,index) =>{
                    if (index===row){
                        element.planning="미승인";
                    }
                })                
                updateData(tmpData);
            }
        }else{
            if (column===4){
                let tmpData = data;
                data[row].produce_history="승인";
                console.log(tmpData);
                updateData(tmpData);
            }else if (column===6){
                let tmpData = data;
                data[row].produce_standard="승인";
                console.log(tmpData);
                updateData(tmpData);
            }else{
                let tmpData = data;
                data[row].planning="승인";
                console.log(tmpData);
                updateData(tmpData);
            }
        }

    };

    const handleSubmit = (event) => {
        event.preventDefault();
        onSubmit(event.target.elements.filter.value);
    };

    return(
        <div class="selected-list">

            <div class="tabel1">
                <ApprovelistTable1 columns={columns} data={data} changeData = {changeData} />
            </div>
        </div>
    );
}



export default Approvelist;
