import {useEffect, useState, useRef} from "react";
import styles from "./UploadDataNavBar.module.css";
import ExcelUpload from './ExcelUpload.js';

function UploadDataNavBar({changeUploadMenu}){

    const clickMenu = (event) =>{
        let clickedMenu = event.target.innerText;
        if (clickedMenu==="생산 준비 현황 Dashboard"){
            changeUploadMenu(0);
        }else if (clickedMenu==="자재 준비 현황"){
            changeUploadMenu(1);
        }else if (clickedMenu==="문서 승인 현황"){
            changeUploadMenu(2);
        }else{
            changeUploadMenu(3);
        }
    };

    return(
        <div class={styles.navBar}>
            <div>
                <h3 class={styles.activeMenu}>생산 계획</h3>
            </div>
            <div class={styles.navbar__under__vl}></div>
            <div>
                <h3>마스터</h3>
            </div>
            <div class={styles.navbar__under__vl}></div>
            <div>
                <h3>BOM</h3>
            </div>
            <div class={styles.navbar__under__vl}></div>
            <div>
                <h3>재고</h3>
            </div>
            <div class={styles.navbar__under__vl}></div>
            <div>
                <h3>예약 내역</h3>
            </div>
            <div class={styles.navbar__under__vl}></div>
            <div>
                <h3>구매 내역</h3>
            </div>
            <div class={styles.navbar__under__vl}></div>
            <div>
                <h3>시험 내역</h3>
            </div>
        </div>
    );
}

export default UploadDataNavBar;
