import React from "react";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import { faUser, faHouseChimney,faKey,faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import styles from "./Search1.module.css";

function Search1({ onSubmit }) {
    const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(event.target.elements.filter.value);
    };

    return (
        <form onSubmit={handleSubmit}>
            <label class={styles.search__box__name} htmlFor='search-input'>자재 검색</label>

            <input name="filter" class={styles.search__box__input} id="search-input" placeholder='원하는 검색 항목을 입력하세요'/>
            <button>
                <FontAwesomeIcon icon={faMagnifyingGlass} />
            </button>
        </form>
    );
}

export default Search1;
