import {useEffect, useState} from "react";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import { faUser, faHouseChimney,faKey,faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import styles from "./SelectedList1.module.css";
import table from "./Table";
import faker from "faker/locale/ko";

function SelectedList1(){

    const columns = ["Name", "Email", "Phone"];

    const data = Array(53)
    .fill()
    .map(() => ({
        name: faker.name.lastName() + faker.name.firstName(),
        email: faker.internet.email(),
        phone: faker.phone.phoneNumber(),
    }));

    return(
        <div class="selected-list">
            <h2>선택항목 리스트</h2>
            <div class={styles.search}>
                <div class={styles.search__box}>
                    <label class={styles.search__box__name} htmlFor='code-input'>자재코드</label>
                    <div class={styles.search__box__inputBox}>
                        <input class={styles.search__box__input} id="code-input" placeholder="자재코드를 입력하세요"/>
                        <button class={styles.search__box__inputIcon}>
                            <FontAwesomeIcon icon={faMagnifyingGlass} />
                        </button>
                    </div>

                </div>
                <div class={styles.search__box}>
                    <label class={styles.search__box__name} htmlFor='name-input'>자재명</label>
                    <div class={styles.search__box__inputBox}>
                        <input class={styles.search__box__input} id="name-input" placeholder="자재명을 입력하세요"/>
                        <button class={styles.search__box__inputIcon}>
                            <FontAwesomeIcon icon={faMagnifyingGlass} />
                        </button>
                    </div>
                </div>
            </div>
            <div class="table">
                <Table columns={columns} data={data} />
            </div>
        </div>
    );
}

export default SelectedList1;
