import {useEffect, useState, useMemo} from "react";
import Table1 from "./Table1.js";


function Approvelist({onSubmit}){
    
    const columns = useMemo(()=>[
        {
            accessor: "division",
            Header: "구분",
        },
        {
            accessor: "material_type",
            Header: "자재 유형",
        },
        {
            accessor: "material_code",
            Header: "자재",
        },
        {
            accessor: "material_description",
            Header: "자재 내역",
        },
        {
            accessor: "produce_history",
            Header: "제품 지시 기록서",
        },
        {
            accessor: "produce_history_date",
            Header: "날짜",
        },
        {
            accessor: "produce_standard",
            Header: "제품표준서",
        },
        {
            accessor: "produce_standard_date",
            Header: "날짜",
        },
        {
            accessor: "planning",
            Header: "생산 계획서",
        },
        {
            accessor: "planning_date",
            Header: "날짜",
        },
    ],[]);

    

    const data = [{
        division:"계획", material_type:"HALB", material_code:"8000004", material_description:"에어탈정(아세클로페낙)반제품", 
        produce_history:"승인", produce_history_date:"2022.10.22", produce_standard:"미승인", produce_standard_date:"2022.10.30", planning:"승인", planning_date:"2022.09.27", 
        },
    ];

    const handleSubmit = (event) => {
        event.preventDefault();
        onSubmit(event.target.elements.filter.value);
    };

    return(
        <div class="selected-list">

            <div class="tabel1">
                <Table1  columns={columns} data={data} />
            </div>
        </div>
    );
}



export default Approvelist;
